package basic;

public class ArithmeticOperations2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   //++ - increment operatot i=i+1;
   //-- - decrement opereator i=i-1;
		
		System.out.println("++++++++++++++++++++++++++++++++++++++++");
		int i=10;
		System.out.println(i++); //post increment - first the value is printed and incremented
		System.out.println(i);  //this prints the incremented value
		
		int j=100;
		System.out.println(++j); //pre increment - first the value is incremented then printed
		System.out.println(j);
	
		System.out.println("________________________________________");
		
		int ii=10;
		System.out.println(ii--); //post decrement - first the value is printed then decremented
		System.out.println(ii); //this prints the decremented value
		
		int jj=100;
		System.out.println(--jj); //pre decrement - first is decremented then printed
		System.out.println(jj);
		
		
		
		
		
	}

}
