package curie;

public class variabledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		//valid identifiers/names
		//a valid identifier will always start with a character or $, _ and it can have a number in between.
		int age;
		int _age;
		int $age;
		int a2g2ge;
		int _ag$ge;
		
		//invalid identifiers
		// int 2age;
		// int @age;
		// int a g e;
		
		//initialization
		int salary=2147483646;
		int newsalary=salary+1;
		System.out.println(newsalary);
		
		short s=3276_7;
		byte b=127;
		
		double d=23.45;//64 bit
		float f=45.23f;//32 bit
		
		char c='a';
		
		boolean boo=true;
		boolean boo2=false;
		
		String str="hello world";
		
		
		
	
		
		
		
		
		
	}

}
